  const [user] = useAuthState(auth)
  let id = useParams()
  return (
    <div>
      <Header/>
      <DndProvider backend={HTML5Backend}>
        <Router>

          {
            !user ? (
              <PublicRoutes />
            ) : (
              <PrivateRoutes user={user} />
            )
          }

        </Router>
      </DndProvider>
    </div>
  )
 }

