import React, { useState, useContext, useEffect } from 'react';
import {useNavigate} from 'react-router-dom';

import { AccountContext } from './Accounts';

export default function Status() {
  const [status, setStatus] = useState(false);

  const { getSession, logout , curentUser} = useContext(AccountContext);
  const navigate = useNavigate();
  const user = curentUser()
  console.log("current user",user)


  // useEffect(() => {

  // getSession()
  //     .then(session => {
  //       console.log('Session:', session);
  //       setStatus(true);
  //     })
  //     .catch(err => {
  //       console.error('failled to return user session')
  //     })

  //   }, []);


  const NavigateLogin = () =>{
    logout().then(data =>{
      console.log('user out',data)
      setStatus(false)
      navigate('/')
    }).catch ( err=>{
       console.error('failled to logout')
    })

  }

  return (
    <div >
      {status ? (
        <div>
          You are logged in.
          <button onClick={NavigateLogin}>Logout</button>
        </div>
      ) : (
        <div>
          You are logged out.
          <button onClick={NavigateLogin}>Logout</button>
        </div>
      )}
    </div>
  );
};
