import React, { useState, useContext, useEffect } from 'react';
import { AccountContext } from '../Accounts'
import { Link, useNavigate } from "react-router-dom"
import logo from "./ucmlogo.png"
import Login from '../Login';

export const Header = (data) =>{

    const [showModeratorBoard, setShowModeratorBoard] = useState(false);
    const [showAdminBoard, setShowAdminBoard] = useState(false);
    const [currentUser1, setCurrentUser1] = useState(undefined);

    const navigate = useNavigate()
    const {  logout,currentUser} = useContext(AccountContext);
    console.log("user in header 11", data)






    if (data){

        console.log("user in header", data)
        setCurrentUser1(data)
        setShowAdminBoard(true)
    }


    const signOut = (e) =>{
        e.preventDefault()
        logout();
        navigate('/')
        window.location.reload();
        setShowModeratorBoard(false);
        setShowAdminBoard(false);
        setCurrentUser1(undefined);

    }

    return (



<div>
      <nav className="navbar navbar-expand navbar-dark bg-primary">
        <Link to={"/"} className="navbar-brand">
          Lamine Sunga
        </Link>
        <div className="navbar-nav mr-auto">
          <li className="nav-item">

              Home

          </li>

          {showModeratorBoard && (
            <li className="nav-item">

                Moderator Board

            </li>
          )}

          {showAdminBoard && (
            <li className="nav-item">

                Admin Board

            </li>
          )}

        </div>

        {currentUser1 ? (
          <div className="navbar-nav ml-auto">
            <li className="nav-item">
              <Link to={"/status"} className="nav-link">
                Lamine
              </Link>
            </li>
            <li className="nav-item">

            <a href="/" className="nav-link" onClick={signOut}>
                LogOut
              </a>

            </li>
          </div>
        ) : (
          <div className="navbar-nav ml-auto">
            <li className="nav-item">

                Login

            </li>

            <li className="nav-item">

                Sign Up 

                <a href="/" className="nav-link" onClick={signOut}>
                LogOut
              </a>

            </li>
          </div>
        )}
      </nav>



    </div>


    )


}





